%% function null = mark (from Dave)% Carl Tape, 19-AUG-2002%%% puts value of 1/x at part marked by mouse click% this file is copies from mark1x.m in the folder tides%function null = mark% use 'int2str' if you want an integer% use 'num2str' if you want decimal% display the frequency OR period: disp(1/p(1,1))
%set(gcf,'WindowButtonDownfcn',[...	'p=get(gca,''CurrentPoint'');',...	'x=num2str(1/p(1,1));',...	'y=num2str(1/p(1,2));',...	'space=0;',...	'disp(p(1,1));',...	'text(p(1,1),p(1,2)+space,x,',...			'''HorizontalAlignment'',''center'',',...			'''FontSize'',12,''Color'',''r'')'])if 0 == 1,% puts value of 1/y at part marked by mouse clickset(gcf,'WindowButtonDownfcn',[...	'p = get(gca,''CurrentPoint'');',...	'x = int2str(1/p(1,1));',...	'y = int2str(1/p(1,2));',...	'space = 0;',...	'disp(p(1,1));',...	'text(p(1,1), p(1,2)+space, y,',...			'''HorizontalAlignment'',''center'',',...			'''FontSize'',12,''Color'',''r'')'])end%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%